import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';

//CHallange three-Creating simple greeting website showing real time 

let time=new Date();
time=time.getHours();
let greeting='';
if(time>=1 && time<11){
      greeting=<h1 style={{color:'orange'}}>Good Morning</h1>;
}
else if(time>11 && time<=19){
  greeting=<h1 style={{color:'green'}}>Good Afternoon</h1>;
}
else {
  greeting=<h1 style={{color:'red'}} >Good Evening</h1>
}
ReactDOM.render(<>
              <>
                    <h1 className='style2'>Hello Bacho,<span style={back}>{greeting}</span></h1>
              </>

</>,document.getElementById("root")
);
