import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';

//CHallange three-Creating simple greeting website showing real time 

const style2={
        marginTop:'50%',
        border : '3px solid black',
        borderRadius:'10px',
        margin: '25% 25% 25% 25%',
        width: '50%',
        textAlign:'center',
        backgroundColor:'pink',

}
let time=new Date();
time=time.getHours();
let greeting='';
if(time>=1 && time<11){
      greeting=<h3 style={{color:'orange',background}}>Good Morning</h3>;
}
else if(time>11 && time<=19){
  greeting=<h3 style={{color:'green'}}>Good Afternoon</h3>;
}
else {
  greeting=<h3 style={{color:'red'}} >Good Evening</h3>
}
ReactDOM.render(<>
              <div>
                <container>
                    <h1 style={style2}>Hello Bacho,{greeting}{time}</h1>
                  </container> 
              </div>

</>,document.getElementById("third")
);
