import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';

//CHallange three-Creating simple greeting website showing real time 

let time=new Date();
time=time.getHours();
let greeting='';
if(time>=1 && time<11){
      greeting=<h3 style={{color:'orange',backgroundColor:'pink'}}>Good Morning</h3>;
}
else if(time>11 && time<=19){
  greeting=<h3 style={{color:'green',backgroundColor:'pink'}}>Good Afternoon</h3>;
}
else {
  greeting=<h3 style={{color:'red',backgroundColor:'pink'}} >Good Evening</h3>
}
ReactDOM.render(<>
              <div>
                    <h1 className='style2'>Hello Bacho,{greeting}</h1>
              </div>

</>,document.getElementById("third")
);
